import React, { Component } from 'react';
import { ContentDiv, Content } from '../styles/SideBarStyles';
import Expand from 'react-expand-animated';
import TodoForm from './TodoForm'

export class TodoStacks extends Component {
    constructor(props){
        super(props);
        this.state = {
            open:false,
        }
        this.toggleTodo = this.toggleTodo.bind(this);
    }
    toggleTodo =() => {
        this.setState((prevState) => ({
            open:!prevState.open,
        }))
    }
    createTodo(item) {
        return (
            <Content onClick={this.toggle} key={item.key}>
                <h2>{item.text[0]}</h2>
                <i>type:{item.text[1]}</i>
            </Content>
        )
    }
    render() {
        var items = this.props.entries;
        var listedItems = items.map(this.createTodo)

            return (
                <React.Fragment>
                    <ContentDiv>
                        {listedItems}
                        <Expand open={this.state.open}>
                            <TodoForm />
                        </Expand>
                    </ContentDiv>
                </React.Fragment>
            )
    }
}

    export default TodoStacks
