import React, { Component } from 'react'
import { MenuDiv, MenuList, MenuItem } from '../styles/ContextMenu'

export class ContextMenu extends Component {
    constructor(props) {
        super(props)
        this.state = {
            contextMenu: false,
            x:0,
            y:0,
        }
        this.menu = document.getElementById('menuDiv');
         
    }
    componentDidMount(){
        window.addEventListener('contextmenu', e => {
            e.preventDefault()
            var clickx = e.clientX;
            var clicky = e.clientY
            this.setState((prevState) => ({
                contextMenu: !prevState.contextMenu,
                x: clickx,
                y:clicky,
            }))
        
        })
    }
    componentDidUpdate(){
        console.log(this.menu.tagName)
        // if(this.state.contextMenu === true){}
        // this.menu.style.left = this.state.x +'px'
        // this.menu.style.top = this.state.y +'px'
    }

    render() {
        return (
            <MenuDiv>
                <MenuList>
                    <MenuItem>Add</MenuItem>
                    <MenuItem>Delete</MenuItem>
                    <MenuItem>Expand</MenuItem>
                </MenuList>
            </MenuDiv>
        )
    }
}

export default ContextMenu
