import Styled from 'styled-components';

const FormDiv = Styled.div`
    display:block;
`;

const AddType = Styled.input`
    display:block;
    width: 90%;
    margin: auto;
    
`;

const AddDesc = Styled.textarea`
    resize: none;
    display:block;
    width: 90%;
    margin: auto;
`;

const AddName = Styled.input`
    display:block;
    width: 90%;
    margin: auto;
`;

const AddDiv = Styled.div`
    display:block;
`;

const AddFormButton = Styled.button`

    display:inline-block;
    margin-left: 10px;
    border-radius:50%;
    border: none;
    background-color: blue;


`;

const AddForm = Styled.form.attrs((props) =>({
    id:'AddForm'
}))`
    width: 100%;
    display:none;

`;

const AddLabel = Styled.label`
    font-size: 40px;
    @media(max-width: 2960px;){
        font-size:10px;
    }
`;

export { FormDiv, AddType, AddDesc, AddName, AddFormButton, AddDiv, AddLabel, AddForm }
