import React, { Component } from 'react'
import { FormDiv, AddType, AddDesc, AddName, AddFormButton, AddDiv, AddLabel, AddForm } from '../styles/AddFormStyles'
import {TodoStacks} from "./TodoStacks"
export class TodoAdd extends Component {
    constructor(props) {
        super();
        this.state = {
            names:[],
            types: [],
            descs: [],
        }
        this.expandForm = this.expandForm.bind(this);
        this.addStack = this.addStack.bind(this);
    }
    expandForm = e => {
        var b = document.getElementById("AddForm");
        if (b.style.display === 'block') {
            b.style.display = 'none';
        } else {
            b.style.display = 'block';
        }
    }
    addStack = e =>{
        if(this._nameBox.value!==""){
            var newState= {
                newNames: this._nameBox.value,
                newType: this._typeBox.value,
                newDesc: this._descBox.value,
                key: Date.now(),
            }
        };
        this.setState((prevState) => ({
            names: prevState.names.concat(newNames),
            type: prevState.types.concat(newType),
            descs: prevState.descs.concat(newDesc),
            key: key
        }))
        console.log(this.state.key);
        e.preventDefault();
    }

    render() {
        const state = this.state;
        return (
            <React.Fragment>
                <AddDiv>
                    <AddLabel>Add Todo Books</AddLabel>
                    <AddFormButton onClick={this.expandForm}>+</AddFormButton>
                    <FormDiv>
                        <AddForm onSubmit={this.addStack}>
                            <AddName ref={(a)=>this._nameBox=a} />
                            <AddType ref={(b)=>this._typeBox=b}/>
                            <AddDesc ref={(c)=>this._descBox=c} />
                            <AddFormButton>Add</AddFormButton>
                        </AddForm>
                    </FormDiv>
                </AddDiv>
                <TodoStacks entries={state} />
            </React.Fragment>
        )
    }
}

export default TodoAdd
